#define VecX vector
#define MapX unordered_map
#define Key tuple<int, bool, bool, bool, int, int>
#define F(x, y, z) for (int x = y; x < z; ++x)
struct KeyHash {
    size_t operator()(const Key& k) const {
        auto [p, t, s, h, S, P] = k;
        size_t hashVal = hash<int>()(p) ^ hash<bool>()(t) ^ hash<bool>()(s) ^
                         hash<bool>()(h) ^ hash<int>()(S) ^ hash<int>()(P);
        return hashVal;
    }
};

struct D {
    VecX<int> d;
    int n;
    MapX<Key, int, KeyHash> m;

    D(const VecX<int>& digs) : d(digs), n(digs.size()) {}

    Key k(int p, bool t, bool s, bool h, int S, int P) const {
        return Key{p, t, s, h, S, P};
    }

    int c() {
        auto dp = [&](auto&& self, int p, bool t, bool s, bool h, int S,
                      int P) -> int {
            if (p == n)
                return s && (h || (P % S == 0)) ? 1 : 0;
            Key key = k(p, t, s, h, S, P);
            if (m.count(key))
                return m[key];

            int res = 0, lim = t ? d[p] : 9;
            auto f = [&](int x) {
                bool nt = t && (x == lim), ns = s || (x != 0), nh = h;
                int nS = S, nP = P;
                if (!ns)
                    return self(self, p + 1, nt, false, false, 0, 1);
                if (x == 0) {
                    nh = true;
                    nP = 0;
                } else if (!nh) {
                    nS += x;
                    nP *= x;
                }
                return self(self, p + 1, nt, true, nh, nS, nP);
            };

            F(x, 0, lim + 1) res += f(x);
            return m[key] = res;
        };

        return dp(dp, 0, true, false, false, 0, 1);
    }
};

struct C {
    static VecX<int> e(int num) {
        VecX<int> digs;
        while (num) {
            digs.insert(digs.begin(), num % 10);
            num /= 10;
        }
        return digs;
    }

    int c(int n) { return D(e(n)).c(); }
};

struct R {
    int c(int l, int r) {
        C cnt;
        return cnt.c(r) - (l > 1 ? cnt.c(l - 1) : 0);
    }
};

class Solution {
public:
    int beautifulNumbers(int l, int r) { return R().c(l, r); }
};

