                            ans += dp(pos + 1, new_tight, True, True, s + d, 0)
                        else:
                            if d == 0:
                                ans += dp(pos + 1, new_tight, True, True, s, 0)
                            else:
                                ans += dp(pos + 1, new_tight, True, False, s + d, p * d)
                return ans

            return dp(0, True, False, False, 0, 1)

        return count(r) - count(l - 1)

