import java.util.*;

class F {
    int[][] fac;
    F() {
        fac = new int[82][];
        for (int s = 1; s <= 81; s++) {
            int x = s;
            int e2 = 0, e3 = 0, e5 = 0, e7 = 0;
            int t = x;
            while(t % 2 == 0) { e2++; t /= 2; }
            while(t % 3 == 0) { e3++; t /= 3; }
            while(t % 5 == 0) { e5++; t /= 5; }
            while(t % 7 == 0) { e7++; t /= 7; }
            if(t == 1) fac[s] = new int[]{e2, e3, e5, e7};
            else fac[s] = null;
        }
    }
}

class State {
    int pos, tight, st, hz, sum, e2, e3, e5, e7;
    State(int pos, int tight, int st, int hz, int sum, int e2, int e3, int e5, int e7) {
        this.pos = pos; this.tight = tight; this.st = st; this.hz = hz; this.sum = sum;
        this.e2 = e2; this.e3 = e3; this.e5 = e5; this.e7 = e7;
    }
    public boolean equals(Object o) {
        if(this == o) return true;
        if(!(o instanceof State)) return false;
        State s = (State)o;
        return pos==s.pos && tight==s.tight && st==s.st && hz==s.hz && sum==s.sum &&
               e2==s.e2 && e3==s.e3 && e5==s.e5 && e7==s.e7;
    }
    public int hashCode() {
        int ok = pos;
        ok = ok * 31 + tight;
        ok = ok * 31 + st;
        ok = ok * 31 + hz;
        ok = ok * 31 + sum;
        ok = ok * 31 + e2;
        ok = ok * 31 + e3;
        ok = ok * 31 + e5;
        ok = ok * 31 + e7;
        return ok;
    }
}

class DP {
    int[] digs;
    int L;
    HashMap<State, Long> memo;
    F fObj;
    static int[][] fac;
    static {
        fac = new int[10][4];
        fac[0] = new int[]{0,0,0,0}; 
        fac[1] = new int[]{0,0,0,0};
        fac[2] = new int[]{1,0,0,0};
        fac[3] = new int[]{0,1,0,0};
        fac[4] = new int[]{2,0,0,0};
        fac[5] = new int[]{0,0,1,0};
        fac[6] = new int[]{1,1,0,0};
        fac[7] = new int[]{0,0,0,1};
        fac[8] = new int[]{3,0,0,0};
        fac[9] = new int[]{0,2,0,0};
    }

    DP(int[] digs) {
        this.digs = digs;
        L = digs.length;
        memo = new HashMap<>();
        fObj = new F();
    }

    long rec(int pos, int tight, int st, int hz, int sum, int e2, int e3, int e5, int e7) {
        if(pos == L) {
            if(st == 0) return 0; 
            if(sum == 0) return 0; 
            if(hz == 1) return 1;
            int[] req = fObj.fac[sum];
            if(req == null) return 0;
            if(e2 >= req[0] && e3 >= req[1] && e5 >= req[2] && e7 >= req[3])
                return 1;
            else return 0;
        }
        State stt = new State(pos, tight, st, hz, sum, e2, e3, e5, e7);
        if(memo.containsKey(stt)) return memo.get(stt);
        long ans = 0;
        int lim = (tight == 1 ? digs[pos] : 9);
        for (int d = 0; d <= lim; d++) {
            int nt = (tight == 1 && d == lim) ? 1 : 0;
            int nst = st, nhz = hz, nsum = sum, ne2 = e2, ne3 = e3, ne5 = e5, ne7 = e7;
            if(st == 0) {
                if(d == 0) {
                } else {
                    nst = 1;
                    nsum = d;
                    ne2 += fac[d][0];
                    ne3 += fac[d][1];
                    ne5 += fac[d][2];
                    ne7 += fac[d][3];
                }
            } else { 
                nsum = sum + d;
                if(d == 0) {
                    nhz = 1; 
                } else {
                    if(nhz == 0) { 
                        ne2 += fac[d][0];
                        ne3 += fac[d][1];
                        ne5 += fac[d][2];
                        ne7 += fac[d][3];
                    }
                }
            }
            ans += rec(pos + 1, nt, nst, nhz, nsum, ne2, ne3, ne5, ne7);
        }
        memo.put(stt, ans);
        return ans;
    }
}

class T {
    static int[] toDigits(long x) {
        String s = Long.toString(x);
        int n = s.length();
        int[] digs = new int[n];
        for (int i = 0; i < n; i++) {
            digs[i] = s.charAt(i) - '0';
        }
        return digs;
    }
}

class B {
    long countBeautiful(long x) {
        if(x < 1) return 0;
        int[] digs = T.toDigits(x);
        DP dp = new DP(digs);
        return dp.rec(0, 1, 0, 0, 0, 0, 0, 0, 0);
    }
}

public class Solution {
    public int beautifulNumbers(int l, int r) {
        B b = new B();
        long ans = b.countBeautiful(r) - b.countBeautiful(l - 1);
        return (int)ans;
    }
}

