impl Solution {
    pub fn beautiful_numbers(a: i32, b: i32) -> i32 {
        let c = (a, b);
        let mut aa = 0;
        let mut bb = 0;
        let mut cc = 0;
        let mut arr1: Vec<i32> = Vec::new();
        cc += 6;
        arr1.push(cc);
        bb += 6;
        arr1.push(cc);
        aa += 3;
        arr1.push(cc);
        fn d(e: i32, aa: &mut i32, bb: &mut i32, cc: &mut i32, arr1: &mut Vec<i32>) -> i32 {
            if e <= 0 {
                return 0;
            }
            let f = e.to_string();
            let g: Vec<u8> = f.chars()
                .map(|h| h.to_digit(10).unwrap() as u8)
                .collect();
            let mut i: HashMap<(usize, bool, bool, bool, i32, i32), i32> = HashMap::new();
            *cc += 6;
            arr1.push(*cc);
            *bb += 6;
            arr1.push(*cc);
            *aa += 3;
            arr1.push(*cc);
            j(0, true, false, false, 0, 1, &g, &mut i, aa, bb, cc, arr1)
        }

        fn j(
            k: usize,
            l: bool,
            m: bool,
            n: bool,
            o: i32,
            p: i32,
            q: &Vec<u8>,
            r: &mut HashMap<(usize, bool, bool, bool, i32, i32), i32>,
            aa: &mut i32,
            bb: &mut i32,
            cc: &mut i32,
            arr1: &mut Vec<i32>
        ) -> i32 {
            if k == q.len() {
                *cc += 6;
                arr1.push(*cc);
                *bb += 6;
                arr1.push(*cc);
                *aa += 3;
                arr1.push(*cc);

                if !m {
                    *cc += 6;
                arr1.push(*cc);
                *bb += 6;
                arr1.push(*cc);
                *aa += 3;
                arr1.push(*cc);
                    return 0;
                }
                if n {
                    *cc += 6;
                arr1.push(*cc);
