class Solution {
public:
    vector<int> dig;
    map<array<int, 5>, int > memo;
    int dp(int pos, int tight, int sm, int prod, int leading) {
        // cout << pos << dig.size() << endl;
        if (pos == dig.size()) {
            if (sm != 0 && prod % sm == 0) return 1;
            else return 0;
        }

        if (memo.find({pos, tight, sm, prod, leading}) != memo.end()) return memo[{pos, tight, sm, prod, leading}];
        // cout << pos << sm << prod << endl;
        int res = 0;

        int limit = dig[pos];
        if (!tight) limit=9;
        for (int d = 0; d <= limit; d++) {
                int nproduct = 1;
                int nl = leading && (d == 0);
                if (!nl) {
                    nproduct= prod * d;
                }

                res += dp(
                    pos + 1,
                    tight && d == limit,
                    sm + d,
                    nproduct,
                    nl
                );
            }

        return memo[{pos, tight, sm, prod, leading}] = res;
    }

    int beautifulNumbers(int l, int r) {
        dig.clear();
        memo.clear();
        while (r != 0) {
            dig.push_back(r % 10);
            r/=10;
        }
        reverse(dig.begin(), dig.end());
        int above = dp(0, 1, 0, 1, 1);
        // cout <<  'd' << endl;
        // cout << above << endl;
        l--;
        dig.clear();
        while (l != 0) {
            dig.push_back(l % 10);
            l/=10;
        }
        memo.clear();
        reverse(dig.begin(), dig.end());
        return above -  dp(0, 1, 0, 1, 1);
    }
};

