from functools import lru_cache
class Solution:
    def beautifulNumbers(self, l: int, r: int) -> int:
        def countToN(n):
            digits = list(map(int,str(n)))
            length = len(digits)

            @lru_cache(None)
            def dp(pos,is_tight,digit_sum,digit_product,nonzero):
                if pos==length:
                    return 1 if nonzero and digit_product % digit_sum== 0 else 0
                limit = digits[pos] if is_tight else 9
                count =0 

                for d in range(0,limit+1):
                    newpr = digit_product*d if nonzero else d
                    newsum = digit_sum+d
                    count+=dp(pos+1,is_tight and (d==limit),newsum,newpr,nonzero or d>0)
                return count
            return dp(0,True,0,1,False)
        return countToN(r)- countToN(l-1)

