class Solution {
public:
    int beautifulNumbers(int l, int r) {
        ll cand1 = helper(r);
        ll cand0 = helper(l-1);
        return cand1 - cand0;
    }
private:
    ll helper(ll x) {
        if(x <= 0) return 0;
        digits = to_string(x);
        n = digits.size();
        dp.clear();
        return dfs(0, 0, 0, 0, 1, 1);
    }
    struct State {
        int pos, started, hasZero, sum, prod, tight;
        bool operator==(const State &lhs) const {
            return pos==lhs.pos && started==lhs.started 
                && hasZero==lhs.hasZero && sum==lhs.sum && prod==lhs.prod && tight==lhs.tight;
        }
    };
    struct StateHash {
        size_t operator()(const State &s) const {
            size_t res = 17;
            res = res * 31 + hash<int>()(s.pos);
            res = res * 31 + hash<int>()(s.started);
            res = res * 31 + hash<int>()(s.hasZero);
            res = res * 31 + hash<int>()(s.sum);
            res = res * 31 + hash<int>()(s.prod);
            res = res * 31 + hash<int>()(s.tight);
            return res;
        }
    };
    string digits; 
    int n;
    unordered_map<State, ll, StateHash> dp;

    ll qpow(int e) {
        ll res = 1;
        while(e--) res *= 10;
        return res;
    }

    ll dfs(int pos, int started, int hasZero, int sum, int prod, int tight) {
        if(pos == n) {
            if(!started) return 0;
            if(hasZero) return 1;
            return (prod % sum == 0) ? 1ll : 0ll;
        }
        State st = {pos, started, hasZero, sum, prod, tight};
        if(dp.find(st) != dp.end()) return dp[st];

        ll res = 0;
        int limit = tight ? digits[pos]-'0' : 9;
        if(!tight && hasZero && started) {
            return qpow(n-pos);
        }
        rep(d, 0, limit+1) {
            int ntight = (tight && (d==limit)) ? 1 : 0;
            if(!started) {
                if(d==0) {
                    res += dfs(pos+1, 0, 0, 0, 1, ntight);
                } else {
                    res += dfs(pos+1, 1, 0, d, d, ntight);
                }
            } else {
                if(d==0) {
                    res += dfs(pos+1, 1, 1, sum, 0, ntight);
                } else {
                    int nsum = sum + d;
                    int nprod = (hasZero ? 0 : prod * d);
                    res += dfs(pos+1, 1, hasZero, nsum, nprod, ntight);
                }
            }
        }
        dp[st] = res;
        return res;
    }
};

