class Solution {
    public int beautifulNumbers(int l, int r) {
        return countZeroDigitNumbers(l, r) + countNoZeroAndBeautiful(l, r);
    }

    private int countZeroDigitNumbers(int l, int r) {
        return countNumbersWithZero(r) - countNumbersWithZero(l - 1);
    }

    private int countNumbersWithZero(int n) {
        if (n < 0) return 0;
        char[] num = Integer.toString(n).toCharArray();
        int len = num.length;
        Integer[][][][] memo = new Integer[len][2][2][2];
        return digitDP(num, 0, 1, 0, 0, memo);
    }

    private int digitDP(char[] num, int pos, int tight, int started, int hasZero, Integer[][][][] memo) {
        if (pos == num.length) {
            return hasZero == 1 ? 1 : 0;
        }
        if (memo[pos][tight][started][hasZero] != null) {
            return memo[pos][tight][started][hasZero];
        }
        int limit = (tight == 1) ? (num[pos] - '0') : 9;
        int count = 0;
        for (int d = 0; d <= limit; d++) {
            int newTight = (tight == 1 && d == limit) ? 1 : 0;
            int newStarted = (started == 1 || d != 0) ? 1 : 0;
            int newHasZero = (hasZero == 1 || (newStarted == 1 && d == 0)) ? 1 : 0;
            count += digitDP(num, pos + 1, newTight, newStarted, newHasZero, memo);
        }
        memo[pos][tight][started][hasZero] = count;
        return count;
    }

    private int countNoZeroAndBeautiful(int l, int r) {
        return countBeautifulNoZero(r) - countBeautifulNoZero(l - 1);
    }

    private int countBeautifulNoZero(int n) {
        if (n < 1) return 0;
        char[] num = Integer.toString(n).toCharArray();
        int len = num.length;
        return digitDP2(num, 0, 1, 0, 0, 1, new HashMap<>());
    }

    private int digitDP2(char[] num, int pos, int tight, int started, long sum, long product, Map<String, Integer> memo) {
        if (pos == num.length) {
            if (started == 1 && sum != 0 && product % sum == 0) {
                return 1;
            } else {
                return 0;
            }
        }
        String key = pos + "," + tight + "," + started + "," + sum + "," + product;
        if (memo.containsKey(key)) {
            return memo.get(key);
        }
        int limit = (tight == 1) ? (num[pos] - '0') : 9;
        int count = 0;
        for (int d = 0; d <= limit; d++) {
            if (d == 0) {
                if (started == 0) {
                    int newTight = (tight == 1 && d == limit) ? 1 : 0;
                    int newStarted = 0;
                    count += digitDP2(num, pos + 1, newTight, newStarted, sum, product, memo);
                }
                continue;
            }
            int newTight = (tight == 1 && d == limit) ? 1 : 0;
            int newStarted = 1;
            long newSum = sum + d;
            long newProduct = product * d;
            count += digitDP2(num, pos + 1, newTight, newStarted, newSum, newProduct, memo);
        }
        memo.put(key, count);
        return count;

    }
}

