class Solution:
    def beautifulNumbers(self, l: int, r: int) -> int:
        def count_beautiful(n: int) -> int:
            if n < 1:
                return 0
            digits = list(map(int, str(n)))
            @functools.cache
            def dp(pos, tight, started, hasZero, s, p):
                if pos == len(digits):
                    if not started:
                        return 0
                    return 1 if (p % s == 0) else 0
                res = 0
                up = digits[pos] if tight else 9
                for d in range(up + 1):
                    ntight = tight and (d == up)
                    if not started:
                        if d == 0:
                            res += dp(pos + 1, ntight, False, False, 0, 1)
                        else:
                            res += dp(pos + 1, ntight, True, False, d, d)
                    else:
                        if d == 0:
                            res += dp(pos + 1, ntight, True, True, s + 0, 0)
                        else:
                            if hasZero:
                                res += dp(pos + 1, ntight, True, True, s + d, 0)
                            else:
                                res += dp(pos + 1, ntight, True, False, s + d, p * d)
                return res
            return dp(0, True, False, False, 0, 1)
        return count_beautiful(r) - count_beautiful(l - 1)

