int dp[11][2][2][82][82][82];

class Solution {
public:
    int beautifulNumbers(int l, int r) {
        vector<int> ld, rd;

        while (l) {
            ld.push_back(l % 10);
            l /= 10;
        }
        while (r) {
            rd.push_back(r % 10);
            r /= 10;
        }
        int size = max(ld.size(), rd.size());
        while (ld.size() < size) ld.push_back(0);
        while (rd.size() < size) rd.push_back(0);

        reverse(ld.begin(), ld.end());
        reverse(rd.begin(), rd.end());

        memset(dp, 0, sizeof(dp));
        int maxSum = min(size * 9, 81);
        for (int i = 1; i <= maxSum; ++i) {
            dp[0][0][0][i][0][1 % i] = 1;
        }
        for (int i = 0; i < size; ++i) {
            for (int u = 0; u < 2; ++u) {
                for (int v = 0; v < 2; ++v) {
                    for (int sum = 1; sum <= maxSum; ++sum) {
                        for (int curSum = sum; curSum >= 0; --curSum) {
                            if ((sum - curSum) > (size - i) * 9) break;

                            for (int curRem = 0; curRem < sum; ++curRem) {
                                if (!dp[i][u][v][sum][curSum][curRem]) continue;

                                for (int digit = 0; digit < 10; ++digit) {
                                    int nu = u, nv = v;
                                    if (u == 0 && digit < ld[i]) continue;
                                    if (u == 0 && digit > ld[i]) {
                                        nu = 1;
                                    }
                                    if (v == 0 && digit > rd[i]) continue;
                                    if (v == 0 && digit < rd[i]) {
                                        nv = 1;
                                    }

                                    int nextRem = curRem;
                                    int nextSum = curSum + digit;
                                    if (nextSum != 0) {
                                        nextRem = (curRem * digit) % sum;
                                    }

                                    dp[i + 1][nu][nv][sum][nextSum][nextRem] += dp[i][u][v][sum][curSum][curRem];
                                }
                            }
                        }
                    }
                }
            }
        }

        int ans = 0;
        for (int u = 0; u < 2; ++u) {
            for (int v = 0; v < 2; ++v) {
                for (int sum = 1; sum <= maxSum; ++sum) {
                    ans += dp[size][u][v][sum][sum][0];
                }
            }
        }
        return ans;
    }
};

