def count_with_zero(x):
    if x <= 0:
        return 0
    digits = list(map(int, str(x)))
    n = len(digits)
    memo = {}

    def dfs(pos, hasZero, started, less):
        if pos == n:
            return 1 if (started and hasZero) else 0
        if (pos, hasZero, started, less) in memo:
            return memo[(pos, hasZero, started, less)]
        limit = digits[pos] if not less else 9
        res = 0
        for d in range(limit + 1):
            res += dfs(pos + 1, hasZero or (d == 0 and started), started or (d != 0), less or (d < limit))
        memo[(pos, hasZero, started, less)] = res
        return res

    return dfs(0, False, False, False)

def count_nonzero_divisible(x):
    if x <= 0:
        return 0
    digits = list(map(int, str(x)))
    n = len(digits)

    @functools.lru_cache(None)
    def dfs(pos, sumS, prod, started, less):
        if pos == n:
            return 1 if (started and sumS > 0 and prod % sumS == 0) else 0
        limit = digits[pos] if not less else 9
        result = 0
        for d in range(limit + 1):
            if started:
                if d == 0:
                    continue
                result += dfs(pos + 1, sumS + d, prod * d, True, less or (d < limit))
            else:
                if d == 0:
                    result += dfs(pos + 1, sumS, prod, False, less or (d < limit))
                else:
                    result += dfs(pos + 1, d, d, True, less or (d < limit))
        return result

    return dfs(0, 0, 1, False, False)

def count_beautiful(x):
    return count_with_zero(x) + count_nonzero_divisible(x) if x >= 1 else 0
class Solution:
    def beautifulNumbers(self, l: int, r: int) -> int:
        return count_beautiful(r) - count_beautiful(l - 1)

