class Solution:
    def beautifulNumbers(self, l: int, r: int) -> int:
        def helper(x):
            digits = list(map(int, str(x)))
            n = len(digits)
            memo = {}
            def dp(i, tight, started, has_zero, s, p):
                if i == n:
                    if not started:
                        return 0

                    if has_zero:
                        return 1

                    return 1 if (s != 0 and p % s == 0) else 0

                key = (i, tight, started, has_zero, s, p)
                if key in memo:
                    return memo[key]

                res = 0
                maxD = digits[i] if tight else 9
                for d in range(0, maxD + 1):
                    if not started:
                        if d == 0:
                            res += dp(i + 1, tight and (d == maxD), False, False, s, p)
                        else:
                            res += dp(i + 1, tight and (d == maxD), True, False, d, d)
                    else:
                        res += dp(i + 1, tight and (d == maxD), True, has_zero or (d == 0), s + d, p * d if not has_zero else 0)
                memo[key] = res
                return res
            return dp(0, True, False, False, 0, 1)
        return helper(r) - helper(l-1)

