class Solution {
public:
    using ll = long long;
    ll cnt(ll X) {
        if(X < 1) return 0;
        string s = to_string(X);
        int n = s.size();
        vector<array<map<tuple<int,int,int>, ll>, 2>> dp(n+1);
        dp[0][1][{0, 0, 1}] = 1;
        for(int pos = 0; pos < n; pos++){
            int limit = s[pos]-'0';
            for(int tight = 0; tight < 2; tight++){
                for(auto &entry : dp[pos][tight]){
                    auto key = entry.first;
                    ll cnt = entry.second;
                    int started, sum, prod;
                    tie(started, sum, prod) = key;
                    int up = (tight ? limit : 9);
                    for(int d = 0; d <= up; d++){
                        int ntight = (tight && d==up) ? 1 : 0;
                        int nstarted = started;
                        int nsum = sum;
                        int nprod = prod;
                        if(!nstarted){
                            if(d!=0){
                                nstarted = 1;
                                nsum = d;
                                nprod = d;
                            }
                        } else {
                            if(nprod==0 or d==0){
                                nprod = 0;
                                nsum += d;
                            } else {
                                nsum += d;
                                nprod *= d;
                            }
                        }
                        dp[pos+1][ntight][{nstarted, nsum, nprod}] += cnt;
                    }
                }
            }
        }
        ll res = 0;
        for(int tight = 0; tight < 2; tight++){
            for(auto &entry : dp[n][tight]){
                int started, sum, prod;
                tie(started, sum, prod) = entry.first;
                if(started && sum > 0 && prod % sum == 0)
                    res += entry.second;
            }
        }
        return res;
    }

    int beautifulNumbers(int l, int r) {
        return (int)(cnt(r) - cnt(l-1));
    }
};

