class Solution {
public:
    vector<int> a;
    map<array<int, 5>, int> dp;
    int dfs(int cur, int sum, int prod, int lim, int zero) {
        if (cur == -1) {
            return !zero && (prod % sum == 0);
        }
        array<int, 5> st = {cur, sum, prod, lim, zero};
        if (auto it = dp.find(st); it != dp.end()) {
            return it->second;
        }
        int& ans = dp[st];

        int up = lim ? a[cur] : 9;

        for (int i = 0; i <= up; i++) {
            int nprod = prod * i;
            if (zero && !i) nprod = 1;
            ans += dfs(cur - 1, sum + i, nprod, lim && i == up, zero && !i);
        }

        return ans;
    }

    int cal(int x) {
        if (x == 0) return 0;
        a.clear();
        dp.clear();
        while (x) {
            a.push_back(x % 10);
            x /= 10;
        }
        return dfs(a.size() - 1, 0, 1, 1, 1);
    }
    int beautifulNumbers(int l, int r) {
        return cal(r) - cal(l - 1);
    }
};

