#define ll long long

class Solution {
    string numToStr(ll n) {
        return to_string(n);
    }

    struct Key {
        int pos, t, start, sum;
        ll prod;
        bool operator==(const Key &other) const {
            return pos == other.pos && t == other.t && start == other.start && sum == other.sum && prod == other.prod;
        }
    };

    struct KeyHash {
        std::size_t operator()(const Key &k) const {
            auto h1 = std::hash<int>()(k.pos);
            auto h2 = std::hash<int>()(k.t);
            auto h3 = std::hash<int>()(k.start);
            auto h4 = std::hash<int>()(k.sum);
            auto h5 = std::hash<ll>()(k.prod);
            return (((((h1 * 31 + h2) * 31 + h3) * 31 + h4) * 31) + h5);
        }
    };

    string s;
    unordered_map<Key, ll, KeyHash> valid;

    ll dpValid(int pos, int t, int start, int sum, ll prod){
        if(pos == s.size())
            return (start && sum != 0 && prod % sum == 0) ? 1LL : 0LL;

        Key key = {pos, t, start, sum, prod};
        if(valid.find(key) != valid.end())
            return valid[key];

        ll res = 0;
        int limit = t ? (s[pos] - '0') : 9;
        for(int d = 0; d <= limit; d++){
            int nt = (t && d < limit) ? 0 : t;
            int nstart = start;
            int nsum = sum;
            ll nprod = prod;

            if(!start){
                if(d == 0){
                    res += dpValid(pos + 1, nt, 0, 0, 1LL);
                    continue;
                }
                nstart = 1;
                nsum = d;
                nprod = d;
            }else{
                if (d == 0) continue;
                nsum += d;
                nprod *= d;
            }
            res += dpValid(pos + 1, nt, nstart, nsum, nprod);
        }

        valid[key] = res;
        return res;
    }

    int dp[20][2][2];
    bool vis[20][2][2];
    int dpAll(int pos, int t, int start){
        if(pos == s.size()) return start ? 1 : 0;
        if(vis[pos][t][start]) return dp[pos][t][start];

        vis[pos][t][start] = true;
        int res = 0;
        int limit = t ? (s[pos] - '0') : 9;

        for(int d = 0; d <= limit; d++){
            int nt = (t && d < limit) ? 0 : t;
            int nstart = start || (d != 0);
            if(nstart && d == 0) continue;

            res += dpAll(pos + 1, nt, nstart);
        }

        dp[pos][t][start] = res;
        return res;
    }

    ll countBeautiful(ll n){
        if(n <= 0) return 0;

        s = numToStr(n);
        valid.clear();
        ll validNoZero = dpValid(0, 1, 0, 0, 1LL);
        memset(vis, 0, sizeof(vis));
        int totalNoZero = dpAll(0, 1, 0);
        ll withZero = n - totalNoZero;
        return withZero + validNoZero;
    }

public:
    int beautifulNumbers(int l, int r) {
        return (int)(countBeautiful(r) - countBeautiful(l - 1));
    }
};

