#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#define MAXP 12
#define MAXS 83
#define MAXH (1<<17)
#define MAX_PAR 30000

int nd;
int dgt[MAXP];
int pc;
int par[MAX_PAR];

void rec_par(int cur, int dg, int rem) {
if(dg > 9) {
par[pc++] = cur;
return;
}
for (int e = 0; e <= rem; e++) {
int mul = 1;
for (int i = 0; i < e; i++)
mul *= dg;
rec_par(cur * mul, dg + 1, rem - e);
}
}

int cmp_int(const void *a, const void *b) {
int ia = *(int*)a, ib = *(int*)b;
return ia - ib;
}

int get_ix(int x) {
int lo = 0, hi = pc - 1;
while(lo <= hi) {
int mid = (lo + hi) / 2;
if(par[mid] == x)
return mid;
else if(par[mid] < x)
lo = mid + 1;
else
hi = mid - 1;
}
return -1;
}

int init_par() {
pc = 0;
rec_par(1, 2, 9);
qsort(par, pc, sizeof(int), cmp_int);
int np = 0;
for (int i = 0; i < pc; i++) {
if(i == 0 || par[i] != par[i - 1])
par[np++] = par[i];
}
pc = np;
return pc;
}

unsigned int dp_key[MAXH];
long long dp_val[MAXH];

unsigned int make_key(int pos, int t, int st, int sm, int ix) {
return ((unsigned int)pos) | (((unsigned int)t) << 4) | (((unsigned int)st) << 5) | (((unsigned int)sm) << 6) | (((unsigned int)ix) << 13);
}

void dp_clear() {
for (int i = 0; i < MAXH; i++)
dp_key[i] = 0xFFFFFFFF;
}

long long f_beauty(int pos, int t, int st, int sm, int ix) {
if(pos == nd) {
if(st && sm > 0) {
int prod = par[ix];
return (prod % sm == 0) ? 1LL : 0LL;
}
return 0LL;
}
unsigned int key = make_key(pos, t, st, sm, ix);
int h = key & (MAXH - 1);
while(1) {
if(dp_key[h] == 0xFFFFFFFF)
break;
if(dp_key[h] == key)
return dp_val[h];
h = (h + 1) & (MAXH - 1);
}
int lim = t ? dgt[pos] : 9;
long long res = 0;
for (int x = 0; x <= lim; x++) {
int nt = (t && (x == lim));
if(!st) {
if(x == 0)
res += f_beauty(pos + 1, nt, 0, sm, ix);
else {
int nix;
if(x == 1)
nix = ix;
else {
int newprod = par[ix] * x;
nix = get_ix(newprod);
if(nix < 0)
continue;
}
res += f_beauty(pos + 1, nt, 1, sm + x, (x == 1) ? ix : nix);
}
} else {
if(x == 0)
continue;
int nix;
if(x == 1)
nix = ix;
else {
int newprod = par[ix] * x;
nix = get_ix(newprod);
if(nix < 0)
continue;
}
res += f_beauty(pos + 1, nt, 1, sm + x, (x == 1) ? ix : nix);
}
}
h = key & (MAXH - 1);
while(dp_key[h] != 0xFFFFFFFF)
h = (h + 1) & (MAXH - 1);
dp_key[h] = key;
dp_val[h] = res;
return res;
}

long long F_bt(int x) {
char buf[20];
sprintf(buf, "%d", x);
nd = 0;
for(char *p = buf; *p; p++)
dgt[nd++] = *p - '0';
dp_clear();
int ix0 = get_ix(1);
return f_beauty(0, 1, 0, 0, ix0);
}

long long dp_nz[MAXP][2][2];
char vis_nz[MAXP][2][2];
long long f_nz(int pos, int t, int st) {
if(pos == nd)
return st;
if(vis_nz[pos][t][st])
return dp_nz[pos][t][st];
vis_nz[pos][t][st] = 1;
int lim = t ? dgt[pos] : 9;
long long res = 0;
for(int x = 0; x <= lim; x++) {
int nt = (t && (x == lim));
if(!st) {
if(x == 0)
res += f_nz(pos + 1, nt, 0);
else
res += f_nz(pos + 1, nt, 1);
} else {
if(x == 0)
continue;
res += f_nz(pos + 1, nt, 1);
}
}
dp_nz[pos][t][st] = res;
return res;
}

long long F_nz(int x) {
char buf[20];
sprintf(buf, "%d", x);
nd = 0;
for(char *p = buf; *p; p++)
dgt[nd++] = *p - '0';
memset(vis_nz, 0, sizeof(vis_nz));
return f_nz(0, 1, 0);
}

int beautifulNumbers(int L, int R) {
if(pc == 0)
init_par();
long long tot = (long long)R - L + 1;
long long nz = F_nz(R) - F_nz(L - 1);
long long bt = F_bt(R) - F_bt(L - 1);
long long ans = (tot - nz) + bt;
return (int)ans;
}

