class Solution {
public:
    struct State {
    int pos;
    bool started;
    int sum;
    long long prod;
    bool tight;
    bool operator==(const State &other) const {
        return pos == other.pos && started == other.started &&
               sum == other.sum && prod == other.prod && tight == other.tight;
    }
};

struct StateHash {
    size_t operator()(const State &s) const {
        size_t h1 = hash<int>()(s.pos);
        size_t h2 = hash<int>()(s.sum);
        size_t h3 = hash<long long>()(s.prod);
        size_t h4 = hash<bool>()(s.started);
        size_t h5 = hash<bool>()(s.tight);
        return (((h1 ^ (h2 << 1)) >> 1) ^ (h3 << 1)) ^ (h4 << 2) ^ (h5 << 3);
    }
};

int beautifulNumbers(int l, int r) {
    auto countBeautiful = [&](int x) -> long long {
        string s = to_string(x);
        unordered_map<State, long long, StateHash> memo;
        function<long long(int, bool, int, long long, bool)> dp = [&](int pos, bool started, int sum, long long prod, bool tight) -> long long {
            if(pos == s.size()){
                // Only count if a number was formed and sum != 0 and product divisible by sum.
                return (started && sum != 0 && prod % sum == 0) ? 1LL : 0LL;
            }
            State st{pos, started, sum, prod, tight};
            if(memo.count(st)) return memo[st];
            int limit = tight ? s[pos] - '0' : 9;
            long long ways = 0;
            for(int d = 0; d <= limit; d++){
                bool ntight = tight && (d == limit);
                if(!started && d == 0) {
                    ways += dp(pos+1, false, 0, 1, ntight);
                } else {
                    int nsum = sum + d;
                    long long nprod = (started ? prod : 1LL) * d;
                    ways += dp(pos+1, true, nsum, nprod, ntight);
                }
            }
            memo[st] = ways;
            return ways;
        };
        return dp(0, false, 0, 1, true);
    };

    long long ans = countBeautiful(r) - (l > 1 ? countBeautiful(l - 1) : 0);
    return (int)ans;
}
};

