class Solution:
    def beautifulNumbers(self, l: int, r: int) -> int:
        ls = (l, r)  

        def solve(x):
            if x < 1:
                return 0
            s = str(x)
            n = len(s)

            @lru_cache(None)
            def dp(pos, tight, start, zero, dsum, prod):
                if pos == n:
                    if not start:
                        return 0  
                    return 1 if zero or (dsum != 0 and prod % dsum == 0) else 0

                ans = 0
                lim = int(s[pos]) if tight else 9

                for d in range(lim + 1):
                    ns = tight and (d == lim)
                    if not start:
                        if d == 0:
                            ans += dp(pos + 1, ns, 0, 0, 0, 1)
                        else:
                            ans += dp(pos + 1, ns, 1, 0, d, d)
                    else:
                        if zero:
                            ans += dp(pos + 1, ns, 1, 1, dsum + d, 0)
                        else:
                            if d == 0:
                                ans += dp(pos + 1, ns, 1, 1, dsum, 0)
                            else:
                                ans += dp(pos + 1, ns, 1, 0, dsum + d, prod * d)

                return ans

            return dp(0, 1, 0, 0, 0, 1)

        return solve(ls[1]) - solve(ls[0] - 1)

