from collections import Counter
class Solution:
    def beautifulNumbers(self, start: int, finish: int) -> int:
        def count_bound(n: int) -> int:
            if n < 1:
                return 0
            digits = list(map(int, str(n)))
            length = len(digits)
            dp = {(0, 1, 0): Counter({(0, 1): 1})}
            for pos in range(length):
                next_dp = {}
                lim = digits[pos]
                for (idx, bound, flag), state_counter in dp.items():
                    hi = lim if bound else 9
                    for d in range(hi + 1):
                        new_bound = 1 if (bound and d == hi) else 0
                        new_flag = 1 if (flag or d > 0) else 0
                        key = (pos + 1, new_bound, new_flag)
                        cnt = next_dp.get(key, Counter())
                        for (sm, prod), ways in state_counter.items():
                            if new_flag:
                                ns = sm + d
                                np = prod * d if (prod != 0 and d != 0) else 0
                            else:
                                ns, np = 0, 1
                            cnt[(ns, np)] += ways
                        next_dp[key] = cnt
                dp = next_dp
            total = 0
            for (pos, _, flag), cnt in dp.items():
                if pos == length and flag:
                    for (s, p), ways in cnt.items():
                        if s != 0 and p % s == 0:
                            total += ways
            return total
        return count_bound(finish) - count_bound(start - 1)

