class Solution {
    public int beautifulNumbers(int l, int r) {
        long ans = countBeautiful(r) - countBeautiful(l - 1);
        return (int) ans;
    }

    private long countBeautiful(int x) {
        if(x <= 0) return 0;
        String s = Integer.toString(x);
        Map<String, Long> memoBeautiful = new HashMap<>();
        long countNoZeroBeautiful = dpBeautiful(s, 0, true, false, 0, 1, memoBeautiful);
        Map<String, Long> memoNoZero = new HashMap<>();
        long countNoZero = dpNoZero(s, 0, true, false, memoNoZero);
        long total = x;
        long countWithZero = total - countNoZero;
        return countWithZero + countNoZeroBeautiful;
    }

    private long dpBeautiful(String s, int pos, boolean tight, boolean started, int sum, int prod, Map<String, Long> memo) {
        if(pos == s.length()) {
            return (started && sum != 0 && prod % sum == 0) ? 1 : 0;
        }
        String key = pos + "," + (tight ? 1 : 0) + "," + (started ? 1 : 0) + "," + sum + "," + prod;
        if(memo.containsKey(key)) return memo.get(key);
        int limit = tight ? s.charAt(pos) - '0' : 9;
        long ways = 0;
        for(int d = 0; d <= limit; d++){
            boolean n_tight = tight && (d == limit);
            if(!started) {
                if(d == 0) ways += dpBeautiful(s, pos+1, n_tight, false, sum, prod, memo);
                else ways += dpBeautiful(s, pos+1, n_tight, true, sum+d, d, memo);
            }else{
                if(d == 0) continue;
                ways += dpBeautiful(s, pos+1, n_tight, true, sum+d, prod*d, memo);
            }
        }
        memo.put(key, ways);
        return ways;
    }

    private long dpNoZero(String s, int pos, boolean tight, boolean started, Map<String, Long> memo) {
        if(pos == s.length()) return started ? 1 : 0;
        String key = pos + "," + (tight ? 1 : 0) + "," + (started ? 1 : 0);
        if(memo.containsKey(key)) return memo.get(key);
        int limit = tight ? s.charAt(pos) - '0' : 9;
        long ways = 0;
        for(int d = 0; d <= limit; d++){
            boolean n_tight = tight && (d == limit);
            if(!started) {
                if(d == 0) ways += dpNoZero(s, pos+1, n_tight, false, memo);
                else ways += dpNoZero(s, pos+1, n_tight, true, memo);
            }else{
                if(d == 0) continue;
                ways += dpNoZero(s, pos+1, n_tight, true, memo);
            }
        }
        memo.put(key, ways);
        return ways;
    }
}

