int dp[10][2][2][2][7][5][3][3][82];
vector<int> pws[10];

class Solution {
public:

    int f(string &s, int pos, bool lead, bool tight, bool zero, int p2, int p3, int p5, int p7, int sum) {

        p2 = min(p2, 6);
        p3 = min(p3, 4);
        p5 = min(p5, 2);
        p7 = min(p7, 2);

        if(pos == s.size())
        {
             if(sum == 0) return 0;

             int prod = zero ? 0 : 1;
             for(int i = 0; i < p2; ++i) prod = (prod * 2) % sum;
             for(int i = 0; i < p3; ++i) prod = (prod * 3) % sum;
             for(int i = 0; i < p5; ++i) prod = (prod * 5) % sum;
             for(int i = 0; i < p7; ++i) prod = (prod * 7) % sum;

             return prod % sum == 0;
        }

        int &ans = dp[pos][lead][tight][zero][p2][p3][p5][p7][sum]; if(ans != -1) return ans;
        ans = 0;

        int lb = 0, ub = tight ? s[pos] - '0' : 9;
        for(int i = lb; i <= ub; ++i)
        {
            ans += f(s, 
                pos + 1, 
                lead and i == 0,
                tight and i == ub, 
                zero or ((!lead) and i == 0), 
                p2 + pws[i][0], 
                p3 + pws[i][1], 
                p5 + pws[i][2], 
                p7 + pws[i][3], 
                sum + i
            );
        }

        return ans;
    }

    int beautifulNumbers(int l, int r) {

        pws[0] = {0, 0, 0, 0};

        for(int i = 1; i < 10; ++i)
        {
            pws[i].clear();

            int temp = i, idx = 0;
            for(int j : {2, 3, 5, 7})
            {
                int cnt = 0;
                while(temp % j == 0)
                {
                    temp /= j;
                    ++cnt;
                }
                pws[i].push_back(cnt);
            }
        }

        auto R = to_string(r), L = to_string(l - 1);

        memset(dp, -1, sizeof dp);
        int ans = f(R, 0, true, true, false, 0, 0, 0, 0, 0);

        memset(dp, -1, sizeof dp);
        ans -= f(L, 0, true, true, false, 0, 0, 0, 0, 0);

        return ans;
    }
};

