@cache
def helper2(rstr: str, s: int, index: int, mod: int, remain_sum: int, flag: bool, flag2: bool) -> int:
    length = len(rstr)
    remains = length - index
    if remains == 0:
        if mod == 0 and remain_sum == 0:
            return 1
        return 0
    if remain_sum > remains * 9:
        return 0
    if remain_sum < 0:
        return 0
    up = 9 if flag else int(rstr[index])
    up = min(up, remain_sum)
    ans = 0
    for i in range(0, up + 1):
        new_flag = flag or i < up
        new_flag2 = flag2 or i > 0
        if new_flag2:
            newmod = (mod * i) % s
        else:
            newmod = 1
        ans += helper2(rstr, s, index + 1, newmod, remain_sum - i, new_flag, new_flag2)
    return ans

@cache
def helper(r: int) -> int:
    if r == 0:
        return 0
    ans = 0
    rstr = str(r)
    for s in range(1, 82):
        temp = helper2(rstr, s, 0, 1, s, False, False)
        # print(r, s, temp)
        ans += temp
    return ans

class Solution:
    def beautifulNumbers(self, l: int, r: int) -> int:
        return helper(r) - helper(l - 1)

