def count_beautiful_upto(X):
    digits = list(map(int, str(X)))
    n = len(digits)
    @lru_cache(maxsize=None)
    def dp(pos, tight, started, s, p):
        if pos == n:
            if started:
                if p == 0:
                    return 1
                else:
                    return 1 if (s != 0 and p % s == 0) else 0
            else:
                return 0
        res = 0
        limit = digits[pos] if tight else 9
        for dig in range(0, limit + 1):
            ntight = tight and (dig == limit)
            if not started:
                if dig == 0:
                    res += dp(pos + 1, ntight, False, 0, 1)
                else:
                    res += dp(pos + 1, ntight, True, dig, dig)
            else:
                ns = s + dig
                if p == 0 or dig == 0:
                    np = 0
                else:
                    np = p * dig
                res += dp(pos + 1, ntight, True, ns, np)
        return res

    return dp(0, True, False, 0, 1)



class Solution:
    def beautifulNumbers(self, l: int, r: int) -> int:
        return count_beautiful_upto(r) - count_beautiful_upto(l - 1)

