class Solution:
    def beautifulNumbers(self, l: int, r: int) -> int:
        @cache
        def dp(s, curr, edge, zero, curr_sum, curr_prod):
            if curr == len(s):
                if curr_sum == 0:
                    return 0
                return curr_prod % curr_sum == 0

            res = 0
            bound = int(s[curr]) if edge else 9

            for digit in range(bound + 1):
                res += dp(s, curr + 1,
                    edge and digit == bound,
                    zero and digit == 0,
                    curr_sum + digit, 
                    curr_prod * digit if not (zero and digit == 0) else curr_prod
                )
            return res

        return dp(str(r), 0, True, True, 0, 1) - dp(str(l - 1), 0, True, True, 0, 1)

