class Solution {
    public:
        int dp[10][91][91][2][2];
        int Solve(const vector<int>& digs, int pos, int curr_prod_mod, int curr_summ, bool is_less, bool is_non_zero,  int sum_digs) {

            if (pos == digs.size()) {
                return curr_summ == sum_digs && curr_prod_mod % sum_digs == 0;
            }
            if (curr_summ > sum_digs) return 0;
            if (dp[pos][curr_prod_mod][curr_summ][is_less][is_non_zero] != -1) {
                return dp[pos][curr_prod_mod][curr_summ][is_less][is_non_zero];
            }
            int ans = 0;
            for(int d=0;d<=9;d++) {
                if(!is_less && d > digs[pos]) break;
                int new_is_non_zero = is_non_zero || d > 0;
                int new_prod_mod = curr_prod_mod % sum_digs;
                if (new_is_non_zero) {
                    new_prod_mod = (new_prod_mod * d) % sum_digs;
                }
                int new_summ = curr_summ + d;
                int new_is_less = is_less || d < digs[pos];
                ans += Solve(digs, pos+1, new_prod_mod, new_summ, new_is_less, new_is_non_zero, sum_digs);
            }
            return dp[pos][curr_prod_mod][curr_summ][is_less][is_non_zero]=ans;
        }

        vector<int> getDigs(int x) {
            vector<int> digs;
            while(x) {
                digs.push_back(x%10);
                x /= 10;
            }
            reverse(digs.begin(), digs.end());
            return digs;
        }

        int Solve(const vector<int>& digs, int sum_digs) {
            for(int pos=0;pos<=digs.size();pos++) {
                for(int curr_prod_mod=0;curr_prod_mod<sum_digs;curr_prod_mod++) {
                    for(int curr_summ=0;curr_summ<=sum_digs;curr_summ++) {
                        for(int is_less=0;is_less<2;is_less++) {
                            for(int is_non_zero=0;is_non_zero<2;is_non_zero++) {
                                dp[pos][curr_prod_mod][curr_summ][is_less][is_non_zero] = -1;
                            }
                        }
                    }
                }
            }
            return Solve(digs, 0, 1 % sum_digs, 0, 0, 0, sum_digs);

        }

        int beautifulNumbers(int l, int r) {
            // sum of the digits can be 1, 2,..., 9*num_digs
            vector<int> rdigs = getDigs(r);
            int ans = 0;
            vector<int> ldigs = getDigs(l-1);
            int max_sum_digs = min(9*(int)rdigs.size(), 81);
            for(int sum_digs=1;sum_digs<=max_sum_digs;sum_digs++) {
                ans += Solve(rdigs, sum_digs);
                if (l > 1)  {
                    ans -= Solve(ldigs, sum_digs);
                }
            }
            return ans;
        }
    };

