class Solution {
public:
    vector<int> ds;
    int go(int pos, bool any, int sum, int prod, map<int, int> dp[10][2][82]) {
        if (pos == ds.size()) {
            return sum > 0 && prod % sum == 0;
        }
        auto& dq = dp[pos][any][sum];
        auto it = dq.find(prod);
        if (it != dq.end()) return it->second;
        int ans = 0;
        int u = any ? 9 : ds[pos];
        for (int i = 0; i <= u; i++) {
            int ns = sum + i;
            ans += go(pos + 1, any || i < ds[pos], sum + i, ns == 0 ? 1 : prod * i, dp);
        }
        dq.emplace_hint(it, prod, ans);
        return ans;
    }
    int solve(int n) {
        if (n == 0) return 0;
        map<int, int> dp[10][2][82];
        ds.clear();
        for (int x = n; x; x /= 10) ds.push_back(x % 10);
        reverse(ds.begin(), ds.end());
        return go(0, 0, 0, 1, dp);
    }
    int beautifulNumbers(int l, int r) {
        int ans = solve(r) - solve(l - 1);
        return ans;
    }
};

