class Solution:
    def beautifulNumbers(self, l: int, r: int) -> int:

        def dp(numStr):
            n = len(numStr)

            @lru_cache(None)
            def dfs(pos, tight, leadingZero, digitSum, digitProduct):
                if pos == n:
                    if leadingZero:
                        return 0  # ignore leading zeros
                    if digitSum == 0:
                        return 0  # avoid division by zero
                    return 1 if digitProduct % digitSum == 0 else 0

                res = 0
                limit = int(numStr[pos]) if tight else 9

                for d in range(0, limit + 1):
                    newTight = tight and (d == limit)
                    newLeadingZero = leadingZero and d == 0
                    newSum = digitSum + d if not newLeadingZero else 0
                    newProduct = digitProduct * d if not newLeadingZero else 1
                    res += dfs(pos + 1, newTight, newLeadingZero, newSum, newProduct)

                return res

            return dfs(0, True, True, 0, 1)

        def count(x):
            return dp(str(x))

        return count(r) - count(l - 1)

