using i64 = long long;
vector<int> digits;
int n; 
unordered_map<i64, int> nine, zero;

class Solution {
public:

    //zip
    i64 encode9(int pos, int isLimit, int sum, int product) {
        return ((i64)product << 12) | ((i64)sum << 5) | ((i64)isLimit << 4) | pos;
    }

    i64 encode0(int pos, int isLimit) {
        return ((i64)isLimit << 4) | pos;
    }

    int dp_withzero(int pos, int isLimit) {
        if (pos == n) return 1;
        i64 key = encode0(pos, isLimit);
        if (zero.find(key) != zero.end()) return zero[key];
        int ways = 0;
        int limit = (isLimit ? digits[pos] : 9);
        for (int d = 0; d <= limit; d++){
            int updatedLimit = (isLimit && (d == limit)) ? 1 : 0;
            ways += dp_withzero(pos+1, updatedLimit);
        }
        zero[key] = ways;
        return ways;
    }

    int dp_nozero(int pos, int isLimit, int sum, int product) {
        if (pos == n) {
            if (sum == 0) return 0;
            return (product % sum == 0) ? 1 : 0;
        }
        i64 key = encode9(pos, isLimit, sum, product);
        if (nine.find(key) != nine.end()) return nine[key];
        int ways = 0;
        int limit = (isLimit ? digits[pos] : 9);
        for (int d = 0; d <= limit; d++){
            int updatedLimit = (isLimit && (d == limit)) ? 1 : 0;
            if (sum == 0 && d == 0) {
                ways += dp_nozero(pos+1, updatedLimit, 0, 1);
            } else if (d == 0) {
                ways += dp_withzero(pos+1, updatedLimit);
            } else {
                int nsum = sum + d;
                int nprod = product * d;
                ways += dp_nozero(pos+1, updatedLimit, nsum, nprod);
            }
        }
        nine[key] = ways;
        return ways;
    }

    int countBeautiful(i64 x) {
        if(x < 1) return 0;
        string sx = to_string(x);
        n = sx.size();
        digits.resize(n);
        for (int i = 0; i < n; i++){
            digits[i] = sx[i] - '0';
        }
        nine.clear();
        zero.clear();
        return dp_nozero(0, 1, 0, 1);
    }

    int beautifulNumbers(int l, int r) {
        return countBeautiful(r) - countBeautiful(l - 1);
    }
};

