class Solution {
public:

	int gino(bool lead, bool sig, int pos, int sum, int cursum, int pro) {
		if (cursum > sum) return 0;
		if (pos == n) return (cursum == sum and pro == 0);
		int &ans = dp[lead][sig][pos][cursum][pro];
		if (ans != -1) return ans;
		ans = 0;

		if (sig) {
			fo(dig, 0, 9) {
				bool nwLead = (lead & (dig == 0));
				ans += gino(nwLead, 1, pos + 1, sum, cursum + dig, nwLead ? 1 : (pro * dig) % sum);
			}
		}
		else {
			int upto = s[pos] - '0';
			fo(dig, 0, upto - 1) {
				bool nwLead = (lead & (dig == 0));
				ans += gino(nwLead, 1, pos + 1, sum, cursum + dig, nwLead ? 1 : (pro * dig) % sum);
			}
			bool nwLead = (lead & (upto == 0));
			ans += gino(nwLead, 0, pos + 1, sum, cursum + upto, nwLead ? 1 : (pro * upto) % sum);
		}
		return ans;
	}

	int compute(int X) {
		s = to_string(X);
		n = sz(s);
		int res = 0;

		fo(sum, 1, n*9) {
			fo(i, 0, 1) {
				fo(j, 0, n - 1) {
					fo(s, 0, sum) {
						fo(rem, 0, sum) {
							dp[0][i][j][s][rem] = dp[1][i][j][s][rem] = -1;
						}
					}

				}
			}
			res += gino(1, 0, 0, sum, 0, 1);
		}
		return res;
	}

	int beautifulNumbers(int l, int r) {
		return compute(r) - (r > 1 ? compute(l - 1) : 0);
	}
};

