constexpr int MAXN = 12;
constexpr int MAXM = 110;

int d[MAXM], dp[MAXN][MAXM][MAXM][2];

int DFS(int sum, int p, int s, int r, int z, bool lim) {
  // printf("sum = %d, p = %d, s = %d, r = %d, z = %d, lim = %dn", sum, p, s, r, z, (int)lim);
  if (p == -1) {
    return s == sum && r == 0;
  }
  if (!lim && ~dp[p][s][r][z]) {
    return dp[p][s][r][z];
  }
  int m = lim ? d[p] : 9;
  int ret = 0;
  for (int i = 0; i <= m; ++i) {
    if (s + i > sum) {
      continue;
    }
    int rr = (z ? i : i * r) % sum;
    int cur = DFS(sum, p - 1, s + i, rr, z && i == 0, lim && i == m);
    ret += cur;
  }
  if (!lim) {
    dp[p][s][r][z] = ret;
  }
  return ret;
}

int Calc(int n) {
  int m = 0, ret = 0;
  for (int i = n; i > 0; i /= 10) {
    d[m++] = i % 10;
  }
  for (int i = 1; i < 100; ++i) {
    memset(dp, -1, sizeof(dp));
    int cur = DFS(i, m - 1, 0, 1, 1, true);
    ret += cur;
    // printf("i = %d, cur = %dn", i, cur);
  }
  // printf("n = %d, ret = %dn", n, ret);
  return ret;
}

class Solution {
public:
    int beautifulNumbers(int l, int r) {
      int ll = Calc(l - 1);
      int rr = Calc(r);
      return rr - ll;
    }
};

