class Solution {
public:
    int beautifulNumbers(int l, int r) {
        string s = to_string(l);
        string t = to_string(r);
        s = string(t.size() - s.size(), '0') + s;
        int n = s.size();
        for(int tight1 = 0; tight1 < 2; tight1++) {
            for(int tight2 = 0; tight2 < 2; tight2++) {
                for(int i = 0; i < n; i++) {
                    for(int sm = 0; sm < 100; sm++) {
                        map<int, int>().swap(dp[tight1][tight2][i][sm]);
                    }
                }
            }
        }
        auto dfs = [&](auto& dfs, int i = 0, bool tight1 = true, bool tight2 = true, int prod = 1, int sm = 0) -> int {
            if(i == n) return prod % sm == 0;
            auto& curr = dp[tight1][tight2][i][sm];
            if(curr.count(prod)) return curr[prod];
            int low = tight1 ? s[i] - '0' : 0; 
            int high = tight2 ? t[i] - '0' : 9;
            int res = 0;
            for(int digit = low; digit <= high; digit++) {
                res += dfs(dfs, i + 1, tight1 && digit == low, tight2 && digit == high, (sm == 0 && digit == 0 ? prod : prod * digit), sm + digit);
            }
            return curr[prod] = res;
        };
        return dfs(dfs);
    }
};

