from functools import cache
class Solution:
    def beautifulNumbers(self, l: int, r: int) -> int:
        def count_beautiful(num):
            n = len(num)

            @cache
            def dfs(pos, s, p, tight, leading):
                if pos == n:
                    if leading:
                        return 0
                    return int(s > 0 and p % s == 0)

                res = 0
                max_digit = int(num[pos]) if tight else 9

                for d in range(max_digit + 1):
                    new_s = s + d
                    if leading and d == 0:
                        new_p = 1
                    else:
                        new_p = p * d
                    res += dfs(pos + 1, new_s, new_p, tight and d == max_digit, leading and d == 0)
                return res

            return dfs(0, 0, 1, True, True)

        l, r = str(l-1), str(r)
        return count_beautiful(r) - count_beautiful(l)

