class Solution
{
public:
  int beautifulNumbers(int c1, int c)
  {
    return solve(c) - solve(c1 - 1);
  }

private:
  int calc(const vector<int> &d1, int pos, int t, int st, int sum, int p1, map<long long, int> &mp1)
  {
    long long it = pos, l1, ans = 0;
    if (pos == d1.size())
    {
      if (!st)
      {
        return 0;
      }
      if ((sum && p1 % sum == 0))
      {
        return 1;
      }
      else
      {
        return 0;
      }
    }
    it = it | (((long long)t << 4) | ((long long)st << 5) | ((long long)sum << 6) | ((long long)p1 << 13));

    if (mp1.count(it))
    {
      return mp1[it];
    }
    if (t)
    {
      l1 = d1[pos];
    }
    else
    {
      l1 = 9;
    }
    for (int x = 0; x <= l1; x++)
    {
      int nt;
      if (t && x == l1)
      {
        nt = 1;
      }
      else
      {
        nt = 0;
      }
      int ns;
      if (st || x != 0)
      {
        ns = 1;
      }
      else
      {
        ns = 0;
      }
      int ss;
      if (ns)
      {
        ss = sum + x;
      }
      else
      {
        ss = 0;
      }
      int np;
      if (ns)
      {
        if (st)
        {
          np = p1 * x;
        }
        else
        {
          np = x;
        }
      }
      else
      {
        np = 1;
      }
      ans = ans + calc(d1, pos + 1, nt, ns, ss, np, mp1);
    }
    return mp1[it] = ans;
  }
  int solve(int x)
  {
    if (x < 0)
      return 0;
    vector<int> d1;
    while (x)
    {
      d1.push_back(x % 10);
      x /= 10;
    }
    reverse(d1.begin(), d1.end());
    map<long long, int> mp1;
    return calc(d1, 0, 1, 0, 0, 1, mp1);
  }
};

