from functools import lru_cache

class Solution:
    def beautifulNumbers(self, l: int, r: int) -> int:
        def count_beautiful(n: int) -> int:
            digits = list(map(int, str(n)))
            length = len(digits)

            @lru_cache(None)
            def dp(pos: int, tight: bool, digit_sum: int, digit_product: int, leading_zero: bool) -> int:
                if pos == length:
                    return 1 if digit_sum != 0 and digit_product % digit_sum == 0 else 0

                limit = digits[pos] if tight else 9
                total = 0

                for d in range(0, limit + 1):
                    total += dp(
                        pos + 1,
                        tight and (d == limit),
                        digit_sum + d if not leading_zero or d != 0 else 0,
                        digit_product * d if not leading_zero or d != 0 else 1,
                        leading_zero and d == 0
                    )
                return total

            return dp(0, True, 0, 1, True)

        return count_beautiful(r) - count_beautiful(l - 1)

