from functools import lru_cache
class Solution:
    def beautifulNumbers(self, l: int, r: int) -> int:
        # prolly some kind of DP since nt q
        # 0s in number = trivially beautiful number, trim search path

        # store prime factorization instead of actual number to avoid slowdown
        # if sum ends up having a prime factor > largest single-digit prime (7) then fail
        def helper(x):
            num = [int(c) for c in str(x)]
            n = len(num)
            # exc means next digit < r corresponding digit
            # can unset exc if we choose smaller digit at same place (e.g. 199 < 200)
            @lru_cache(maxsize=None)
            def dfs(i, s, p, exc, nonzero):
                if i == n:
                    return 1 if (nonzero and s != 0 and p % s == 0) else 0
                res = 0
                rlim = num[i]+1 if exc else 10
                for rv in range(rlim):
                    next_exc = exc and (rv == rlim-1)
                    if not nonzero:
                        res += dfs(i+1, rv, rv or 1, next_exc, rv != 0)
                    else:
                        res += dfs(i+1, s + rv, p * rv, next_exc, nonzero)
                return res
            return dfs(0, 0, 1, True, False)
        return helper(r) - helper(l-1)

