class Solution:
    def beautifulNumbers(self, l: int, r: int) -> int:
        def f(n):
            if n <= 0:
                return 0
            s = str(n)
            D = len(s)

            @functools.lru_cache(maxsize=None)
            def g(i, d_sum, d_prod, lesser, nz_seen):
                if i == D:
                    if d_sum == 0:
                        return 0
                    return d_prod % d_sum == 0

                ret = 0
                digit = int(s[i])
                for d in range(10):
                    if lesser or d <= digit:
                        d_prod2 = 1 if (d == 0 and not nz_seen) else d_prod * d
                        ret += g(i + 1, d_sum + d, d_prod2, lesser or (d < digit), 
                                nz_seen or d > 0)
                return ret

            return g(0, 0, 1, False, False)

        return f(r) - f(l - 1)

