class Solution {
public:
    using ll = long long;
    typedef tuple<int, int, int, int, int> Key;
    struct KeyHash {
        size_t operator()(const Key &key) const {
            auto [pos, tight, lead, sum, prod] = key;
            size_t res = 17;
            res = res * 31 + hash<int>()(pos);
            res = res * 31 + hash<int>()(tight);
            res = res * 31 + hash<int>()(lead);
            res = res * 31 + hash<int>()(sum);
            res = res * 31 + hash<int>()(prod);
            return res;
        }
    };

    int beautifulNumbers(int l, int r) {
        auto countBeautiful = [&](int x) -> ll {
            if(x < 1) return 0;
            string s = to_string(x);
            vector<int> digits;
            for (char c : s) digits.push_back(c - '0');
            int n = digits.size();
            unordered_map<Key, ll, KeyHash> memo;
            function<ll(int,int,int,int,int)> dp = [&](int pos, int tight, int lead, int sum, int prod) -> ll {
                if(pos == n) {
                    if(lead) return 0LL;
                    if(sum == 0) return 0LL;
                    return (prod % sum == 0 ? 1LL : 0LL);
                }
                Key key = {pos, tight, lead, sum, prod};
                if(memo.count(key)) return memo[key];
                ll res = 0;
                int limit = tight ? digits[pos] : 9;
                for (int d = 0; d <= limit; d++) {
                    int ntight = (tight && (d == limit)) ? 1 : 0;
                    int nlead = (lead && d == 0) ? 1 : 0;
                    int nsum = sum, nprod = prod;
                    if(nlead) {
                    } else {
                        if(lead) { 
                            nsum = d;
                            nprod = d;
                        } else {
                            nsum = sum + d;
                            nprod = prod * d;
                        }
                    }
                    res += dp(pos + 1, ntight, nlead, nsum, nprod);
                }
                memo[key] = res;
                return res;
            };
            return dp(0, 1, 1, 0, 1);
        };

        ll ans = countBeautiful(r) - countBeautiful(l - 1);
        return (int)ans;
    }
};

