class Solution {
    public int beautifulNumbers(int l, int r) {
        return (int)(f(r) - f(l - 1));
    }
    private long f(int x) {
        if(x < 1) return 0;
        char[] s = Integer.toString(x).toCharArray();
        return dfs(0, 1, 0, 0, 1, s, new HashMap<>());
    }
    private long dfs(int pos, int tight, int started, int sum, int prod, char[] s, Map<String, Long> dp) {
        if(pos == s.length) return started == 1 && sum != 0 && prod % sum == 0 ? 1 : 0;
        String key = pos + "_" + tight + "_" + started + "_" + sum + "_" + prod;
        if(dp.containsKey(key)) return dp.get(key);
        int limit = tight == 1 ? s[pos] - '0' : 9;
        long ans = 0;
        for (int d = 0; d <= limit; d++) {
            int nt = (tight == 1 && d == limit) ? 1 : 0;
            if (started == 0 && d == 0) ans += dfs(pos + 1, nt, 0, 0, 1, s, dp);
            else {
                int ns = sum + d;
                int np = started == 0 ? d : (prod == 0 ? 0 : prod * d);
                ans += dfs(pos + 1, nt, 1, ns, np, s, dp);
            }
        }
        dp.put(key, ans);
        return ans;
    }
}

