class Solution {
    class Status {
        int position, strictness, totalSum, totalProduct;
        boolean numberStarted, hasZero;
        public Status(int position, int strictness, boolean numberStarted, int totalSum, int totalProduct, boolean hasZero) {
            this.position = position;
            this.strictness = strictness;
            this.numberStarted = numberStarted;
            this.totalSum = totalSum;
            this.totalProduct = totalProduct;
            this.hasZero = hasZero;
        }
        @Override
        public int hashCode() {
            int hash = position;
            hash = hash * 31 + strictness;
            hash = hash * 31 + (numberStarted ? 1 : 0);
            hash = hash * 31 + totalSum;
            hash = hash * 31 + totalProduct;
            hash = hash * 31 + (hasZero ? 1 : 0);
            return hash;
        }
        @Override
        public boolean equals(Object o) {
            if (this == o) return true;
            if (!(o instanceof Status)) return false;
            Status other = (Status) o;
            return position == other.position && strictness == other.strictness && numberStarted == other.numberStarted &&
                   totalSum == other.totalSum && totalProduct == other.totalProduct && hasZero == other.hasZero;
        }
    }

    int[] digitsArray;
    Map<Status, Integer> memoizationMap;

    public int countBeautifulUpTo(int upperLimit) {
        if (upperLimit <= 0) return 0;
        String numberString = Integer.toString(upperLimit);
        int length = numberString.length();
        digitsArray = new int[length];
        for (int i = 0; i < length; i++) {
            digitsArray[i] = numberString.charAt(i) - '0';
        }
        memoizationMap = new HashMap<>();
        return dp(0, 1, false, 0, 1, false);
    }

    private int dp(int position, int strictness, boolean numberStarted, int totalSum, int totalProduct, boolean hasZero) {
        if (position == digitsArray.length) {
            if (!numberStarted) return 0;
            if (hasZero) return 1;
            return (totalProduct % totalSum == 0) ? 1 : 0;
        }
        Status status = new Status(position, strictness, numberStarted, totalSum, totalProduct, hasZero);
        if (memoizationMap.containsKey(status)) return memoizationMap.get(status);
        int limit = (strictness == 1) ? digitsArray[position] : 9;
        int answer = 0;
        for (int digit = 0; digit <= limit; digit++) {
            int newStrictness = (strictness == 1 && digit == limit) ? 1 : 0;
            boolean newNumberStarted = numberStarted;
            int newTotalSum = totalSum;
            int newTotalProduct = totalProduct;
            boolean newHasZero = hasZero;
            if (!numberStarted) {
                if (digit != 0) {
                    newNumberStarted = true;
                    newTotalSum = digit;
                    newTotalProduct = digit;
                    newHasZero = false; 
                } else {
                    newNumberStarted = false;
                    newTotalSum = 0;
                    newTotalProduct = 1; 
                    newHasZero = false;
                }
            } else { 
                newTotalSum = totalSum + digit;
                if (newHasZero || digit == 0) {
                    newHasZero = true;
                    newTotalProduct = 0;
                } else {
                    newTotalProduct = totalProduct * digit;
                }
            }
            answer += dp(position + 1, newStrictness, newNumberStarted, newTotalSum, newTotalProduct, newHasZero);
        }
        memoizationMap.put(status, answer);
        return answer;
    }

    public int beautifulNumbers(int lowerLimit, int upperLimit) {
        int storedLowerLimit = lowerLimit;  
        return countBeautifulUpTo(upperLimit) - countBeautifulUpTo(lowerLimit - 1);
    }
}

