class Solution {
    private java.util.Map<String, Long> memo;

    public int beautifulNumbers(int l, int r) {
        long countR = countBeautiful(r);
        long countL = countBeautiful(l - 1);
        return (int)(countR - countL);
    }

    private long countBeautiful(int n) {
        if(n < 1) return 0;
        String s = Integer.toString(n);
        int[] digits = new int[s.length()];
        for (int i = 0; i < s.length(); i++) {
            digits[i] = s.charAt(i) - '0';
        }
        memo = new java.util.HashMap<>();
        return dp(0, 1, false, false, 0, 1, digits);
    }


    private long dp(int pos, int tight, boolean started, boolean hasZero, int sum, long prod, int[] digits) {
        if (pos == digits.length) {
            if (!started) return 0; 
            if (hasZero) return 1; 
            return (prod % sum == 0) ? 1 : 0;
        }

        String key = pos + "_" + tight + "_" + (started ? 1 : 0) + "_" + (hasZero ? 1 : 0) + "_" + sum + "_" + prod;
        if (memo.containsKey(key)) {
            return memo.get(key);
        }

        long ans = 0;
        int limit = (tight == 1) ? digits[pos] : 9;
        for (int d = 0; d <= limit; d++) {
            int newTight = (tight == 1 && d == limit) ? 1 : 0;
            if (!started) {
                if (d == 0) {
                    ans += dp(pos + 1, newTight, false, false, 0, 1, digits);
                } else {
                    ans += dp(pos + 1, newTight, true, false, d, d, digits);
                }
            } else {
                boolean newHasZero = hasZero || (d == 0);
                int newSum = sum + d;
                long newProd = prod * d;
                ans += dp(pos + 1, newTight, true, newHasZero, newSum, newProd, digits);
            }
        }

        memo.put(key, ans);
        return ans;
    }
}

