class Solution {
    String s;
    Long[][][] dp;
    HashMap<String, Long> memo;

    public int beautifulNumbers(int L, int R) {
        return (int)(f(R) - f(L - 1));
    }

    public long f(int n) {
        if(n == 0) return 0;
        return qwe(n) + n - asd(n);
    }

    public long asd(int n) {
        s = Integer.toString(n);
        int len = s.length();
        dp = new Long[len + 1][2][2];
        return rec(0, 1, 0);
    }

    public long rec(int p, int tt, int st) {
        if (p == s.length()) return st == 1 ? 1 : 0;
        if (dp[p][tt][st] != null) return dp[p][tt][st];
        int mx = tt == 1 ? s.charAt(p) - '0' : 9;
        long ans = 0;
        for (int d = 0; d <= mx; d++) {
            int nt = (tt == 1 && d == mx) ? 1 : 0;
            if (st == 0 && d == 0) {
                ans += rec(p + 1, nt, 0);
            } else {
                if (d == 0) continue;
                ans += rec(p + 1, nt, 1);
            }
        }
        return dp[p][tt][st] = ans;
    }

    public long qwe(int n) {
        s = Integer.toString(n);
        memo = new HashMap<>();
        return check(0, 1, 0, 0, 1);
    }

    public long check(int p, int tt, int st, int sum, long prod) {
        if (p == s.length()) {
            return st == 1 && prod % sum == 0 ? 1 : 0;
        }
        String key = p + "_" + tt + "_" + st + "_" + sum + "_" + prod;
        if (memo.containsKey(key)) return memo.get(key);
        int mx = tt == 1 ? s.charAt(p) - '0' : 9;
        long ret = 0;
        for (int d = 0; d <= mx; d++) {
            int nt = (tt == 1 && d == mx) ? 1 : 0;
            if (st == 0 && d == 0) {
                ret += check(p + 1, nt, 0, sum, prod);
            } else {
                if (d == 0) continue;
                ret += check(p + 1, nt, 1, sum + d, prod * d);
            }
        }
        memo.put(key, ret);
        return ret;
    }
}

