            n = len(digs)

            @lru_cache(maxsize=None)
            def dp(i, j, k, x, y):
                if i == n:
                    return 1 if j and x > 0 and y % x == 0 else 0
                ans = 0
                lim = digs[i] if k else 9
                for d in range(lim + 1):
                    if j:
                        if d == 0:
                            continue
                    cond = k and (d == lim)
                    if j:
                        ans += dp(i + 1, True, cond, x + d, y * d)
                    else:
                        if d == 0:
                            ans += dp(i + 1, False, cond, x, y)
                        else:
