class Solution {
    fun beautifulNumbers(l: Int, r: Int): Int {
        var total = 0
        for (s in 1..90) {
            val countR = calculate(s, r)
            val countLMinus1 = calculate(s, l - 1)
            total += countR - countLMinus1
        }
        return total
    }

    private fun calculate(s: Int, upper: Int): Int {
        if (upper < 0) return 0
        val digits = upper.toString().toCharArray().map { it - '0' }
        val n = digits.size
        data class State(val pos: Int, val sum: Int, val prodMod: Int, val isTight: Boolean, val isZero: Boolean)

        val memo = mutableMapOf<State, Int>()

        fun dp(pos: Int, sum: Int, prodMod: Int, isTight: Boolean, isZero: Boolean): Int {
            if (pos == n) {
                return if (sum == s && prodMod == 0) 1 else 0
            }
            val state = State(pos, sum, prodMod, isTight, isZero)
            memo[state]?.let { return it }

            var res = 0
            val maxDigit = if (isTight) digits[pos] else 9

            for (d in 0..maxDigit) {
                val newIsTight = isTight && (d == maxDigit)
                val newIsZero = isZero && (d == 0)

                var newSum: Int
                var newProdMod: Int

                if (isZero) {
                    if (d == 0) {
                        newSum = sum
                        newProdMod = prodMod
                    } else {
                        newSum = d
                        newProdMod = d % s
                        if (newProdMod < 0) newProdMod += s
                    }
                } else {
                    newSum = sum + d
                    if (newSum > s) continue
                    newProdMod = (prodMod * d) % s
                    if (newProdMod < 0) newProdMod += s
                }

                res += dp(pos + 1, newSum, newProdMod, newIsTight, newIsZero)
            }

            memo[state] = res
            return res
        }

        return dp(0, 0, 1, true, true)
    }
}

