#define ll long long
class Solution {
public:
    unordered_map<ll, ll> mp;

    ll encode(int idx, int tight, int start, int sum, ll prod) {
        ll key = idx;
        key = (key << 1) | tight;
        key = (key << 1) | start;
        key = (key << 7) | sum;
        key = (key << 32) | (prod & 0xffffffffLL);
        return key;
    }

    int beautifulNumbers(int L, int R) {
        string sR = to_string(R);
        mp.clear();
        function<ll(int, int, int, int, ll, string&)> dp = 
        [&](int idx, int tight, int started, int sum, ll prod, string &s) -> ll {
            if (idx == s.size())
                return (started && sum > 0 && (prod % sum == 0)) ? 1LL : 0LL;
            ll key = encode(idx, tight, started, sum, prod);
            if (mp.count(key))
                return mp[key];
            ll ans = 0;
            int ub = tight ? s[idx] - '0' : 9;
            for (int dig = 0; dig <= ub; dig++) {
                int ntight = (tight && (dig == ub)) ? 1 : 0;
                int nstarted = started;
                int nsum = sum;
                ll nprod = prod;
                if (!started) {
                    if (dig != 0) {
                        nstarted = 1;
                        nsum = dig;
                        nprod = dig;
                    }
                } else {
                    nsum += dig;
                    nprod *= dig;
                }
                ans += dp(idx + 1, ntight, nstarted, nsum, nprod, s);
            }
            mp[key] = ans;
            return ans;
        };

        ll countR = dp(0, 1, 0, 0, 1, sR);
        ll countL = 0;
        if (L > 1) {
            string sL = to_string(L - 1);
            mp.clear();
            countL = dp(0, 1, 0, 0, 1, sL);
        }
        return (int)(countR - countL);
    }
};

