class Solution {
    public static int MinimumPathSum(int[][] mat) {

        int n = mat.length;
        int m = mat[0].length;
        int[][] dp = new int[n][m];

        for (int i = n - 1; i >= 0; i--) {
            for (int j = m - 1; j >= 0; j--) {
                if (i == n - 1 && j == m - 1) {
                    dp[i][j] = mat[i][j];
                } else if (i == n - 1) {
                    dp[i][j] = mat[i][j] + dp[i][j + 1];
                } else if (j == m - 1) {
                    dp[i][j] = mat[i][j] + dp[i + 1][j];
                } else {
                    dp[i][j] = mat[i][j] + Math.min(dp[i + 1][j], dp[i][j + 1]);
                }
            }
        }
        return dp[0][0];
    }

    static Map<String, Long> memo;
    static String pathString = "1234567890";

    public int beautifulNumbers(int l, int r) {

        int ans = (int) (countBeautiful(r) - countBeautiful(l - 1));
        print(ans);
        return ans;

    }
    public static int min(int a, int b) {
        if (a < b) {
            return a;
        }
        return b;
    }
    public static int max(int a, int b) {
        if (a > b) {
            return a;
        }
        return b;
    }
    public static <E> void print(E res) {
        System.out.println(res);
    }
    public static <E> void printArr(int[] arr) {
        System.out.println(Arrays.toString(arr));
    }

    public static int houseRobber4(int[] nums, int index, int count, int k, int currentCap, int[][][] dp) {

        if (count >= k) {
            return currentCap;
        }

        if (index >= nums.length) {
            return Integer.MAX_VALUE;
        }

        if (dp[index][count][currentCap] != -1) {
            return dp[index][count][currentCap];
        }

        int skip = houseRobber4(nums, index + 1, count, k, currentCap, dp);

        int newCap = Math.max(currentCap, nums[index]);
        int pick = houseRobber4(nums, index + 2, count + 1, k, newCap, dp);

        return dp[index][count][currentCap] = Math.min(skip, pick);
    }

    public static boolean coinChangeCF(int[] coins, int index, int target, int[][] dp) {

        if (target == 0) {
            return true;
        }

        if (index == coins.length) {
            return false;
        }

        if (dp[index][target] != -1) {
            return dp[index][target] == 1;
        }

        boolean Pick = false;

        if (target >= coins[index]) {
            Pick = coinChangeCF(coins, index, target - coins[index], dp);
        }
        boolean notPick = coinChangeCF(coins, index + 1, target, dp);

        boolean res = Pick || notPick;
        dp[index][target] = res ? 1 : 0;
        return res;
    }
    public static boolean isBeautiful(int n) {
        int temp = 0;
        int pro = 1, sum = 0;
        while (n > 0) {
            int rem = n % 10;
            pro *= rem;
            sum += rem;
        }
        return pro % sum == 0;
    }

    public static long countBeautiful(long n) {

        if (n < 1) {
            return 0;
        }
        String s = Long.toString(n);
        int ix = 0;
        int[] ar = new int[s.length()];
        for (int i = 0; i < s.length(); i++) {
            ar[i] = s.charAt(i) - '0';
            ix ++;
        }
        memo = new HashMap<>();
        return solveThis(0, 1, 0, 0, 1, ar);
    }

    public static long solveThis(int pos, int tight, int s, int sum, long pro, int[] digit_arr) {


        long carry = tight & s;
        if (pos == digit_arr.length) {
            if (s == 0) return 0;
            return (sum != 0 && pro % sum == 0) ? 1 : 0;
        }

        String key = pos + "|" + tight + "|" + s + "|" + sum + "|" + pro;
        if (memo.containsKey(key)) {
            return memo.get(key);
        }
        long res = 0;
        print(carry);
        carry = (carry - 1) & 1;
        int ji = (tight == 1 ? digit_arr[pos] : 9);
        for (int d = 0; d <= ji; d++) {
            int y1 = (tight == 1 && d == ji) ? 1 : 0;
            int x1 = s;
            int newSum = sum;
            long newPro = pro;
            if (s == 0 && d == 0) {
                x1 = 0;
                newSum = 0;
                newPro = 1;
            } else if (s == 0) {
                x1 = 1;
                newSum = d;
                newPro = d;
            } else {
                newSum = sum + d;
                newPro = pro * d;
            }

            if (newPro % 3 == 2) {
                carry ++;
            }
            res += solveThis(pos + 1, y1, x1, newSum, newPro, digit_arr);
        }
        memo.put(key, res);
        return res;
    }

    public static int abs(int a) {
        return a < 0 ? -a : a;
    }
    public static int coinChangeLC(int[] coins, int index, int target, int[][] dp) {

        if (index == coins.length - 1) return target % coins[index] == 0 ? target / coins[index] : (int) 1e9;

        if (dp[index][target] != -1) {
            return dp[index][target];
        }

        int Pick = (int) 1e9;

        if (target >= coins[index]) {
            Pick = 1 + coinChangeLC(coins, index, target - coins[index], dp);
        }
        int notPick = 0 + coinChangeLC(coins, index + 1, target, dp);

        return dp[index][target] = Math.min(Pick, notPick);
    }
    public static int coinChangeGFG(int[] coins, int index, int target, int[][] dp) {

        if (index == coins.length - 1) return target % coins[index] == 0 ? 1 : 0;

        if (dp[index][target] != -1) {
            return dp[index][target];
        }

        int Pick = 0;

        if (target >= coins[index]) {
            Pick = 1 + coinChangeGFG(coins, index, target - coins[index], dp);
        }
        int notPick = 0 + coinChangeGFG(coins, index + 1, target, dp);

        return dp[index][target] = Pick + notPick;
    }
    public static int gcd(int a, int b) {
        return b == 0 ? a : gcd(b, a % b);
    }
    public static int lcm(int a, int b) {
        return (a * b) / gcd(a, b);
    }

    public static int frogJump(int[] arr) {

        int n = arr.length;
        int[] dp = new int[n];
        Arrays.fill(dp, Integer.MAX_VALUE);
        dp[0] = 0;

        for (int i = 0; i < n; i++) {
            for (int j = i + 1; j <= i + 2 && j < n; j++) {
                dp[j] = Math.min(dp[j], dp[i] + Math.abs(arr[i] - arr[j]));
            }
        }
        return dp[n - 1];
    }

    public static int minJumps(int[] nums, int index) {

        if (index >= nums.length - 1) {
            return 0;
        }

        int minJumps = 99999;

        for (int i = 1; i <= nums[index]; i++) {
            minJumps = min(minJumps, 1 + minJumps(nums, index + i));
        }
        return minJumps;
    }

    public static int knapSack(int w, int[] values, int[] weights, int index, int[] dp) {

        if (index >= values.length) {
            return 0;
        }

        if (w <= 0) {
            return 0;
        }

        if (dp[index] != -1) {
            return dp[index];
        }

        int Pick = 0;
        if (w >= weights[index]) {
            Pick = values[index] + knapSack(w - weights[index], values, weights, index + 1, dp);
        }
        int notPick = 0 + knapSack(w, values, weights, index + 1, dp);

        return dp[index] = max(Pick, notPick);
    }


    public static boolean isPowerof2(int n) {
        return (n & (n - 1)) == 0;
    }
    public static boolean containsAllOnes(int n) {
        return (n & (n + 1)) == 0;
    }

    public static boolean isPowerof3(int n, int index) {

        if (n == 0) {
            return true;
        }

        if (n < 0 || Math.pow(3, index) > n) {
            return false;
        }

        boolean Pick = isPowerof3(n - (int) Math.pow(3, index), index + 1);
        boolean notPick = isPowerof3(n, index + 1);

        return Pick || notPick;
    }

    public static String longestPalindrome(String s) {

        int n = s.length();
        Boolean[][] memo = new Boolean[n][n];
        int maxLen = 0;
        int start = 0;

        for (int i = 0; i < n; i++) {
            for (int j = i; j < n; j++) {
                if (isPalindrome(s, i, j, memo)) {
                    if (j - i + 1 > maxLen) {
                        maxLen = j - i + 1;
                        start = i;
                    }
                }
            }
        }
        return s.substring(start, start + maxLen);
    }

    private static boolean isPalindrome(String s, int i, int j, Boolean[][] memo) {

        if (i >= j) return true;

        if (memo[i][j] != null) return memo[i][j];

        if (s.charAt(i) == s.charAt(j)) {
            memo[i][j] = isPalindrome(s, i + 1, j - 1, memo);
        } else {
            memo[i][j] = false;
        }

        return memo[i][j];
    }

    public static int MinCostClimbingStairs(int[] cost, int index, int[] dp) {

        if (index >= cost.length) {
            return 0;
        }

        if (dp[index] != 0) {
            return dp[index];
        }

        int one = cost[index] + MinCostClimbingStairs(cost, index + 1, dp);
        int two = cost[index] + MinCostClimbingStairs(cost, index + 2, dp);

        return dp[index] = Math.min(one, two);
    }
    public static int climbingStairs(int n, int[] dp) {

        if (n < 3) {
            return n;
        }

        if (dp[n] != -1) {
            return dp[n];
        }

        int one = climbingStairs(n - 1, dp);
        int two = climbingStairs(n - 2, dp);

        return dp[n] = one + two;
    }
    public static boolean isPalindrome(String s, int i, int j) {

        if (i >= j) {
            return true;
        }

        if (s.charAt(i) != s.charAt(j)) {
            return false;
        }

        return isPalindrome(s, i + 1, j - 1);
    }

    public static boolean isPrime (int n) {

        if (n == 1) {
            return false;
        }

        for (int i = 2; i * i <= n; i++) {
            if (n % i == 0) {
                return false;
            }
        }
        return true;
    }
    public static boolean canEat(int[] piles, int h, int k) {

        int hours = 0;

        for (int pile : piles) {
            int div = pile / k;
            hours += div;
            if (pile % k != 0) {
                hours ++;
            }
        }
        return hours <= h;
    }

    public static int abcAsSubString(String s) {

        int[] count = new int[3];
        int ans = 0;
        int left = 0;

        for (int i = 0; i < s.length(); i++) {
            count[s.charAt(i) - 'a'] ++;
            while (count[0] > 0 && count[1] > 0 && count[2] > 0) {
                ans += s.length() - i;
                count[s.charAt(left) - 'a'] --;
                left ++;
            }
        }
        return ans;
    }

    public static boolean abcfinder(String s) {
        return s.contains("a") && s.contains("b") && s.contains("c");
    }
    public static int longestPalindromicSubseq(String s, int i, int j, int[][] dp) {

        if (i > j) {
            return 0;
        }

        if (i == j) {
            return 1;
        }

        if (dp[i][j] != 0) {
            return dp[i][j];
        }

        if (s.charAt(i) == s.charAt(j)) {
            return 2 + longestPalindromicSubseq(s, i + 1, j - 1, dp);
        }

        int a = longestPalindromicSubseq(s, i + 1, j, dp);
        int b = longestPalindromicSubseq(s, i, j - 1, dp);

        return dp[i][j] = Math.max(a, b);
    }
    public static void permutations1(int[] nums, int index, List<Integer> running, List<List<Integer>> ans) {

        // base case
        if (index >= nums.length) {
            ans.add(new ArrayList<>(running));
            return; 
        }

        for (int i = 0; i < nums.length; i++) {
            if (running.contains(nums[i])) continue;
            running.add(nums[i]);
            permutations1(nums, index + 1, running, ans);
            running.remove(running.size() - 1);
        }
    }

    public static int lcs(String s1, String s2, int i, int j, int[][] dp)  {

        if (i == s1.length() || j == s2.length()) {
            return 0;
        }

        if (dp[i][j] != -1) {
            return dp[i][j];
        }

        if (s1.charAt(i) == s2.charAt(j)) {
            return 1 + lcs(s1, s2, i + 1, j + 1, dp);
        }

        int op1 = lcs(s1, s2, i + 1, j, dp);
        int op2 = lcs(s1, s2, i, j + 1, dp);

        return dp[i][j] = Math.max(op1, op2);
    }

    public static int[] dailyTemperatures(int[] temperatures) {


        int n = temperatures.length;
        int[] ans = new int[temperatures.length];
        Stack<Integer> stack = new Stack<>();

        for (int i = n - 1; i >= 0; i--) {
            while (!stack.isEmpty() && temperatures[stack.peek()] <= temperatures[i]) {
                stack.pop();
            }
            if (stack.isEmpty()) {
                ans[i] = 0;
            } else {
                ans[i] = stack.peek() - i;
            }
            stack.add(i);
        }
        return ans;
    }
    public static int[] NGE2(int[] nums) {

        int n = nums.length;
        int[] ans = new int[n];
        int index = n - 1;

        Stack<Integer> stack = new Stack<>();
        for (int i = (2 * n) - 1; i >= 0; i--) {
            while (!stack.isEmpty() && stack.peek() <= nums[i % n]) {
                stack.pop();
            }
            if (i < n) {
                if (stack.isEmpty()) {
                    ans[index--] = -1;
                } else {
                    ans[index--] = stack.peek();
                }
            }

            stack.add(nums[i % n]);
        }
        return ans;
    }
    public static int[] NGE1 (int[] arr) {

        int n = arr.length;
        int index = n - 1;
        int[] ans = new int[n];

        Stack<Integer> stack = new Stack<>(); // []


        for (int i = n - 1; i >= 0; i--) {

            while (!stack.isEmpty() && stack.peek() <= arr[i]) {
                stack.pop();
            }
            if (stack.isEmpty()) {
                ans[index] = -1;
            } else {
                ans[index] = stack.peek();
            }
            stack.add(arr[i]);
            index --;
        }
        return ans;
    }
    public static int maxAreaWater(int[] heights) {

        int s = 0;
        int e = heights.length - 1;

        int nasDaily = 0;

        while (s < e) {
            int ans = Math.min(heights[s], heights[e]);
            int idx = e - s;
            nasDaily = Math.max(nasDaily, ans * idx);
            if (heights[s] < heights[e]) {
                s ++;
            } else {
                e --;
            }
        }
        return nasDaily;
    }
    public static long coloredCell (int n) {

        if (n == 1) {
            return n;
        }

        return (n - 1) * 4 + coloredCell(n - 1);
    }
}

