def count_beautiful(n):
    if n < 1: return 0
    digits = list(map(int, str(n)))
    L = len(digits)

    @cache
    def dp(pos, tight, started, beautiful, s, p):
        if pos == L:
            if not started:
                return 0
            if beautiful:
                return 1
            else:
                return int(s != 0 and p % s == 0)

        res = 0
        max_digit = digits[pos] if tight else 9
        for d in range(max_digit + 1):
            new_tight = tight and (d == max_digit)
            if not started:
                if d == 0:
                    res += dp(pos + 1, new_tight, False, False, 0, 1)
                else:
                    res += dp(pos + 1, new_tight, True, False, d, d)
            else:
                if beautiful:
                    res += dp(pos + 1, new_tight, True, True, 0, 0)
                else:
                    if d == 0:
                        res += dp(pos + 1, new_tight, True, True, 0, 0)
                    else:
                        res += dp(pos + 1, new_tight, True, False, s + d, p * d)
        return res

    return dp(0, True, False, False, 0, 1)

class Solution:
    def beautifulNumbers(self, l: int, r: int) -> int:
        return count_beautiful(r) - count_beautiful(l - 1)

