class Solution {
public:
    int beautifulNumbers(int l, int r) {
        return f(r) - f(l - 1);
    }
private:
    vector<vector<vector<unordered_map<int, int>>>> d;
    int f(int n) {
        if (n == 0) return 0;
        vector<int> v;
        while (n > 0) {
            v.push_back(n % 10);
            n /= 10;
        }
        reverse(v.begin(), v.end());
        d.assign(v.size(), vector<vector<unordered_map<int, int>>>(100, vector<unordered_map<int, int>>(2)));
        return g(0, 0, 1, 1, v);
    }
    int g(int p, int s, int m, int t, vector<int>& v) {
        if (p == v.size()) return (s != 0 && m % s == 0);
        if (d[p][s][t].count(m)) return d[p][s][t][m];
        int l = t ? v[p] : 9, c = 0;
        for (int x = 0; x <= l; x++) {
            c += g(p + 1, s + x, (x == 0 && s == 0) ? 1 : m * x, t && (x == l), v);
        }
        return d[p][s][t][m] = c;
    }
};

