class Solution:
    def beautifulNumbers(self, l: int, r: int) -> int:
        def f(x):
            s = str(x)
            n = len(s)
            @cache
            def dfs(i, isEq, isNum, c0, c2, c3, c5, c7, y):
                if i == n:
                    if not isNum:
                        return 0
                    if c0:
                        return 1
                    for t,ct in zip([2,3,5,7], [c2,c3,c5,c7]):
                        k = 0
                        while y % t == 0:
                            y //= t
                            k += 1
                        if k > ct:
                            return 0
                    return 1 if y == 1 else 0
                ans = 0
                hi = int(s[i]) if isEq else 9
                for j in range(hi+1):
                    ans += dfs(i+1, isEq and j == hi, isNum or j > 0,
                            (c0 or j == 0) and isNum,
                            min(6, c2 + (j and j % 2 == 0) + (j and j % 4 == 0) + (j == 8)),
                            min(4, c3 + (j and j % 3 == 0) + (j == 9)),
                            min(2, c5 + (j == 5)),
                            min(2, c7 + (j == 7)),
                            y + j)
                return ans

            ans = dfs(0, True, False, False, 0, 0, 0, 0, 0)
            dfs.cache_clear()
            return ans

        return f(r) - f(l-1)

