class Solution:
    def beautifulNumbers(self, l: int, r: int) -> int:
        l = [int(c) for c in str(l-1)]
        r = [int(c) for c in str(r)]
        A = [l, r]
        @cache
        def dp(li, idx, cap, prod, sum, leading_zero=True):
            a = A[li]
            if idx == len(a):
                return prod % sum == 0 if sum else 0
            ret = 0
            mx = 9
            if cap and a[idx] < mx:
                mx = a[idx]
            for digit in range(mx+1):
                iscap = cap and digit == a[idx]
                ret += dp(li, idx+1, iscap, prod * digit if digit != 0 or not leading_zero else prod, sum + digit, leading_zero and digit == 0)
            return ret
        #print(dp(1, 0, True, 1, 0), dp(0, 0, True, 1, 0))
        res = dp(1, 0, True, 1, 0) - dp(0, 0, True, 1, 0)
        dp.cache_clear()
        return res

