int rec(int i, bool smaller, bool bigger,bool zero, int rem,int rem2) {
    if(rem2<0)return 0;
    if (i == n) return rem == 0 && rem2==0;
    int &ret = dp[i][smaller][bigger][zero][rem][rem2];
    if (vis[i][smaller][bigger][zero][rem][rem2] == id) return ret;
    vis[i][smaller][bigger][zero][rem][rem2] = id;

    ret = 0;
    int low = bigger ? 0 : (L[i] - '0');
    int high = smaller ? 9 : (R[i] - '0');

    for (int d = low; d <= high; d++) {
        int z2=zero && (d==0);
        int new_rem =(z2?1:(rem*d)%mod);
        bool new_smaller = smaller || (d < high);
        bool new_bigger = bigger || (d > low);
        ret += rec(i + 1, new_smaller, new_bigger,z2, new_rem,rem2-d);
    }

    return ret;
}
class Solution {
public:
    int beautifulNumbers(int l, int r) {
        L= to_string(l),R= to_string(r);
        while(L.size()<R.size())L='0'+L;
         n=L.size();
        int ans=0;
        for(int sum=1; sum < 100; sum++){
            id++;
            mod=sum;
            ans+=rec(0,0,0,1,1,sum);
        }
        return ans;
    }
};

